function maxPathSum(root) {
    let maxSum = -Infinity;

    function maxGain(node) {
        if (!node) return 0;

        // é€’å½’è®¡ç®—å·¦å³å­æ ‘çš„æœ€å¤§è´¡çŒ®å€¼
        // å¦‚æœè´¡çŒ®å€¼ä¸ºè´Ÿï¼Œåˆ™ä¸é€‰æ‹©è¯¥å­æ ‘
        const leftGain = Math.max(maxGain(node.left), 0);
        const rightGain = Math.max(maxGain(node.right), 0);

        // å½“å‰èŠ‚ç‚¹çš„æœ€å¤§è·¯å¾„å’Œï¼šèŠ‚ç‚¹å€¼ + å·¦å­æ ‘è´¡çŒ® + å³å­æ ‘è´¡çŒ®
        const currentMax = node.val + leftGain + rightGain;

        // æ›´æ–°å…¨å±€æœ€å¤§è·¯å¾„å’Œ
        maxSum = Math.max(maxSum, currentMax);

        // è¿”å›è¯¥èŠ‚ç‚¹çš„æœ€å¤§è´¡çŒ®å€¼ï¼šèŠ‚ç‚¹å€¼ + max(å·¦å­æ ‘è´¡çŒ®, å³å­æ ‘è´¡çŒ®)
        return node.val + Math.max(leftGain, rightGain);
    }

    maxGain(root);
    return maxSum;
}


