/**
 * å²›å±¿æ•°é‡ - DFSè§£æ³•
 * @param {character[][]} grid
 * @return {number}
 */
function numIslands(grid) {
    if (!grid || grid.length === 0) return 0;

    const rows = grid.length;
    const cols = grid[0].length;
    let count = 0;

    /**
     * æ·±åº¦ä¼˜å…ˆæœç´¢ï¼Œæ ‡è®°ç›¸é‚»çš„é™†åœ°
     * @param {number} row
     * @param {number} col
     */
    function dfs(row, col) {
        // è¾¹ç•Œæ£€æŸ¥å’Œæ°´åŸŸæ£€æŸ¥
        if (row < 0 || row >= rows || col < 0 || col >= cols || grid[row][col] === '0') {
            return;
        }

        // æ ‡è®°ä¸ºå·²è®¿é—®ï¼ˆå˜æˆæ°´åŸŸï¼‰
        grid[row][col] = '0';

        // é€’å½’è®¿é—®å››ä¸ªæ–¹å‘
        dfs(row + 1, col); // ä¸‹
        dfs(row - 1, col); // ä¸Š
        dfs(row, col + 1); // å³
        dfs(row, col - 1); // å·¦
    }

    // éå†æ•´ä¸ªç½‘æ ¼
    for (let i = 0; i < rows; i++) {
        for (let j = 0; j < cols; j++) {
            if (grid[i][j] === '1') {
                count++; // å‘ç°æ–°å²›å±¿
                dfs(i, j); // æ ‡è®°æ•´ä¸ªå²›å±¿
            }
        }
    }

    return count;
}

