function topKFrequent(nums, k) {
    // ç»Ÿè®¡é¢‘ç‡
    const freqMap = new Map();
    for (const num of nums) {
        freqMap.set(num, (freqMap.get(num) || 0) + 1);
    }

    // ä½¿ç”¨å°é¡¶å †ç»´æŠ¤å‰kä¸ªé«˜é¢‘å…ƒç´ 
    class MinHeap {
        constructor() {
            this.heap = [];
        }

        push(item) {
            this.heap.push(item);
            this.heapifyUp(this.heap.length - 1);
        }

        pop() {
            if (this.heap.length === 0) return null;
            const min = this.heap[0];
            const last = this.heap.pop();
            if (this.heap.length > 0) {
                this.heap[0] = last;
                this.heapifyDown(0);
            }
            return min;
        }

        heapifyUp(index) {
            const parent = Math.floor((index - 1) / 2);
            if (parent >= 0 && this.heap[parent][1] > this.heap[index][1]) {
                [this.heap[parent], this.heap[index]] = [this.heap[index], this.heap[parent]];
                this.heapifyUp(parent);
            }
        }

        heapifyDown(index) {
            const left = 2 * index + 1;
            const right = 2 * index + 2;
            let smallest = index;

            if (left < this.heap.length && this.heap[left][1] < this.heap[smallest][1]) {
                smallest = left;
            }
            if (right < this.heap.length && this.heap[right][1] < this.heap[smallest][1]) {
                smallest = right;
            }

            if (smallest !== index) {
                [this.heap[smallest], this.heap[index]] = [this.heap[index], this.heap[smallest]];
                this.heapifyDown(smallest);
            }
        }

        size() {
            return this.heap.length;
        }

        top() {
            return this.heap[0];
        }
    }

    const heap = new MinHeap();

    for (const [num, freq] of freqMap) {
        heap.push([num, freq]);
        if (heap.size() > k) {
            heap.pop();
        }
    }

    const result = [];
    while (heap.size() > 0) {
        result.push(heap.pop()[0]);
    }

    return result;
}


